export const fetchLocationData = async (lat: number, lon: number) => {
  let nearestStadtteil = "";
  let minDistance = Infinity;

  Object.entries(locations).forEach(([stadtteil, coordinates]) => {
    const distance = haversineDistance(
      lat,
      lon,
      coordinates.lat,
      coordinates.lon
    );
    if (distance < minDistance) {
      minDistance = distance;
      nearestStadtteil = stadtteil;
    }
  });
  return nearestStadtteil;
};

export const getCustomIcon = (
  type:
    | "futureLaterne"
    | "futureLaternewerkstatt"
    | "laterne"
    | "flohmarkt"
    | "future"
    | "event"
    | "today"
    | "weihnachtsmarkt"
    | "smallWeihnachtsmarkt"
    | undefined
) =>
  type === "laterne"
    ? laterneIcon
    : type === "futureLaterne"
      ? futureLaterneIcon
      : type === "flohmarkt"
        ? flohmarktIcon
        : type === "futureLaternewerkstatt"
          ? futureLaternewerkstattMarkerIcon
          : type === "future"
            ? futureIcon
            : type === "event"
              ? eventIcon
              : type === "today"
                ? todayIcon
                : type === "weihnachtsmarkt"
                  ? weihnachtsmarktIcon
                  : type === "smallWeihnachtsmarkt"
                    ? smallWeihnachtsmarktIcon
                    : type === "laternewerkstatt"
                      ? laternewerkstattMarkerIcon
                      : divIcon({
                          iconSize: [25, 25],
                          className: "bg-transparent",
                        });
